var e=$("#productscroller"),l=document.getElementById("productscroller"),s=$("#rightcontrol"),i=$("#leftcontrol"),t=0,c=l.scrollWidth-l.clientWidth,n=document.getElementById("clientwidth").clientWidth;$(e).scroll(function(){t=e.scrollLeft()});$(i).click(function(){e.scrollLeft()>0?(t-=n,e.scrollLeft(t)):e.scrollLeft(0)});$(s).click(function(){e.scrollLeft()<c?(t+=n,e.scrollLeft(t)):e.scrollLeft(c)});var d=document.getElementById("open_menu"),a=document.getElementById("nav_close"),r=document.getElementById("menu");$(d).click(function(){r.classList.add("mobile_nav_open")});$(a).click(function(){r.classList.remove("mobile_nav_open")});var o=document.getElementById("show_pass");$(o).click(function(){o.checked?document.getElementById("password").setAttribute("type","text"):document.getElementById("password").setAttribute("type","password")});
